{
    "name_prefix": "{% faker name.prefix %}",
    "first_name": "{% faker name.firstName %}",
    "middle_name": "{% faker name.middleName %}",
    "last_name": "{% faker name.lastName %}",
    "name_suffix": "{% faker name.suffix %}",
    "email": "{% faker internet.email %}",
    "username": "{% faker internet.userName %}",
    // "organization_id": "{% faker datatype.uuid %}",
    "password": "Demo@12345",
    "identification": [
        {
            "type": "SSN",
            "value": "{% faker finance.creditCardNumber %}"
        }
    ],
    "address": [
        {
            "type": "PRIMARY",
            "label": "Home",
            "address": "{% faker address.streetAddress %}",
            "city": "{% faker address.city %}",
            "state": "{% faker address.state %}",
            "country": "{% faker address.country %}",
            "country_code": "{% faker address.countryCode %}",
            "zipcode": "{% faker address.zipCode %}",
            "latitude": "{% faker address.latitude %}",
            "longitude": "{% faker address.longitude %}",
            "offset": "+9:30",
            "zone": "{% faker address.timeZone %}",
            "is_default": true
        },
        {
            "type": "SECONDARY",
            "label": "Apartment",
            "address": "{% faker address.streetAddress %}",
            "city": "{% faker address.city %}",
            "state": "{% faker address.state %}",
            "country": "{% faker address.country %}",
            "country_code": "{% faker address.countryCode %}",
            "zipcode": "{% faker address.zipCode %}",
            "latitude": "{% faker address.latitude %}",
            "longitude": "{% faker address.longitude %}",
            "offset": "+9:30",
            "zone": "{% faker address.timeZone %}",
            "is_default": false
        }
    ],
    "contact": [
        {
            "type": "PRIMARY",
            "label": "Home",
            "country_code": "+01",
            "number": "{% faker phone.number %}",
            "is_default": true
        },
        {
            "type": "SECONDARY",
            "label": "Apartment",
            "country_code": "+01",
            "number": "{% faker phone.number %}",
            "is_default": false
        }
    ],
    "social_profiles": [
        {
            "label": "Facebook",
            "link": "{% faker internet.url %}"
        },
        {
            "label": "Instagream",
            "link": "{% faker internet.url %}"
        },
        {
            "label": "Twitter",
            "link": "{% faker internet.url %}"
        },
        {
            "label": "LinkedIn",
            "link": "{% faker internet.url %}"
        }
    ],
    "avatar": "{% faker image.imageUrl %}",
    "meta_data": {
        "gender": "{% faker name.gender %}",
        "dob": "1989-11-27 18:04:06",
        "theme_code": "DEFAULT",
        "is_super_admin": true
    }
}

